mapper.select:
SELECT spu.id, spu.spu_no as spuNo, spu.goods_name as goodsName, spu.goods_description as goodsDescription, spu.goods_image_key as goodsImageKey, spu.low_price as lowPrice, spu.brand_id as brandId, category.id as categoryId, category_name as categoryName FROM tb_goods_category category JOIN tb_goods_spu spu ON category.id = spu.category_id
WHERE 1=1 and spu.is_delete=0 and category.is_delete = 0
and category.id = #{categoryId}
order by spu.id limit #{offset, jdbcType=INTEGER},#{rowSize, jdbcType=INTEGER}

SELECT spu.id, spu.spu_no as spuNo, spu.goods_name as goodsName, spu.goods_description as goodsDescription, spu.low_price as lowPrice, spu.brand_id as brandId, category.id as categoryId, spec.id as specId, spec.spec_no as specNo, spec.spec_name as specName, spu.goods_image_key as goodsImageKey, spec.spec_description as specDescription, specValue.id as specValueId, specValue.spec_value as specValue, tgss.spec_image_key as goodsSkuImageKey FROM tb_goods_spu_spec tgss JOIN tb_goods_spu spu ON tgss.spu_id = spu.id JOIN tb_goods_spec_value specValue ON tgss.spec_value_id = specValue.id JOIN tb_goods_spec spec ON specValue.spec_id = spec.id JOIN tb_goods_category category ON spu.category_id = category.id WHERE 1=1 and tgss.is_delete =0 and spu.is_delete=0 and specValue.is_delete =0 and spec.is_delete =0 and category.is_delete = 0
and tgss.spu_id = #{spuId}
order by specValue.id limit #{offset, jdbcType=INTEGER},#{rowSize, jdbcType=INTEGER}

SELECT spu.id, spu.spu_no as spuNo, spu.goods_name as goodsName, spu.goods_description as goodsDescription, spu.goods_image_key as goodsImageKey, spu.low_price as lowPrice, spu.brand_id as brandId, spu.category_id as categoryId from tb_goods_spu spu where 1=1
and ( spu.goods_name like concat(concat('%',#{searchKeyWord}),'%') or spu.goods_description like concat(concat('%',#{searchKeyWord}),'%') )
limit #{offset, jdbcType=INTEGER},#{rowSize, jdbcType=INTEGER}
